#!/usr/bin/perl

use 5.010;
use strict;
use warnings;

use Benchmark ':all';

use HTML::Entities;

my $COUNT = 3000;

say "$COUNT iterations under $^V";

my @lines = <DATA>;
chomp @lines;

cmpthese( $COUNT, {
    original => sub {
        my $x;
        for ( @lines ) {
            $x = HTML::Entities::encode_entities($_);
        }
    },
    new => sub {
        my $x;
        for ( @lines ) {
            $x = HTML::Entities::encode_entities($_);
            #$x = _encode_entities($_);
        }
    },
} );


sub _encode_entities
{
    return undef unless defined $_[0];
    my $ref;
    if (defined wantarray) {
	my $x = $_[0];
	$ref = \$x;     # copy
    } else {
	$ref = \$_[0];  # modify in-place
    }
    if (defined $_[1] and length $_[1]) {
        # Not something we care about.
    } else {
	# Encode control chars, high bit chars and '<', '&', '>', ''' and '"'
	$$ref =~ s/([^\n\r\t !\#\$%\(-;=?-~])/$HTML::Entities::char2entity{$1} || HTML::Entities::num_entity($1)/ge;
    }
    $$ref;
}


exit 0;


